mem_phone=#{mem_phone}, mem_addr=#{mem_addr}, mem_grade=#{mem_grade}, mem_image=#{mem_image}, 
mem_logdate=#{mem_logdate}, mem_birth=#{mem_birth}, mem_backno=#{mem_backno}, club_no=#{club_no}, mem_club_regdate=#{mem_club_regdate}	
where mem_no=#{mem_no}
